//! Palette control

use crate::wasm4::PALETTE;

/// Screen palette
///
/// WASM-4 uses a 4-colour palette, i.e. at a given time there can only be at
/// most 4 different colours on the screen.
///
/// This enum defines several 4-colour palettes.
// Generate colour placeholders at https://placeholderimage.dev/
pub enum Palette {
    /// Default palette of WASM-4
    ///
    /// 1. `#def7cd` <img src="data:image/png;base64,">
    /// 2. `#86bf6b` <img src="data:image/png;base64,">
    /// 3. `#306950` <img src="data:image/png;base64,">
    /// 4. `#071821` <img src="data:image/png;base64,">
    Default,

    /// [Gold GB](https://lospec.com/palette-list/gold-gb) palette
    ///
    /// 1. `#cfab51` <img src="data:image/png;base64,">
    /// 2. `#9d654c` <img src="data:image/png;base64,">
    /// 3. `#4d222c` <img src="data:image/png;base64,">
    /// 4. `#210b1b` <img src="data:image/png;base64,">
    Gold,

    /// [Ice Cream GB](https://lospec.com/palette-list/ice-cream-gb) palette
    ///
    /// 1. `#fff6d3` <img src="data:image/png;base64,">
    /// 2. `#f9a875` <img src="data:image/png;base64,">
    /// 3. `#eb6b6f` <img src="data:image/png;base64,">
    /// 4. `#7c3f58` <img src="data:image/png;base64,">
    IceCream,

    /// [Hollow](https://lospec.com/palette-list/hollow) palette
    ///
    /// 1. `#fafbf6` <img src="data:image/png;base64,">
    /// 2. `#c6b7be` <img src="data:image/png;base64,">
    /// 3. `#565a75` <img src="data:image/png;base64,">
    /// 4. `#0f0f1b` <img src="data:image/png;base64,">
    Hollow,

    /// [Wheat GB](https://lospec.com/palette-list/wheat-gb) palette
    ///
    /// 1. `#fffad6` <img src="data:image/png;base64,">
    /// 2. `#e6c12b` <img src="data:image/png;base64,">
    /// 3. `#7a3921` <img src="data:image/png;base64,">
    /// 4. `#240024` <img src="data:image/png;base64,">
    Wheat,

    /// [Rustic GB](https://lospec.com/palette-list/rustic-gb) palette
    ///
    /// 1. `#edb4a1` <img src="data:image/png;base64,">
    /// 2. `#a96868` <img src="data:image/png;base64,">
    /// 3. `#764462` <img src="data:image/png;base64,">
    /// 4. `#2c2137` <img src="data:image/png;base64,">
    Rustic,

    /// [DustByte](https://lospec.com/palette-list/dustbyte) palette
    ///
    /// 1. `#f5e9bf` <img src="data:image/png;base64,">
    /// 2. `#aa644d` <img src="data:image/png;base64,">
    /// 3. `#788374` <img src="data:image/png;base64,">
    /// 4. `#372a39` <img src="data:image/png;base64,">
    DustByte,
}

impl Palette {
    /// Set the current palette
    pub fn set(&self) {
        let quadruple = match self {
            Palette::Default => [0xdef7cd, 0x86bf6b, 0x306950, 0x071821],
            Palette::Gold => [0xcfab51, 0x9d654c, 0x4d222c, 0x210b1b],
            Palette::IceCream => [0xfff6d3, 0xf9a875, 0xeb6b6f, 0x7c3f58],
            Palette::Hollow => [0xfafbf6, 0xc6b7be, 0x565a75, 0x0f0f1b],
            Palette::Wheat => [0xfffad6, 0xe6c12b, 0x7a3921, 0x240024],
            Palette::Rustic => [0xedb4a1, 0xa96868, 0x764462, 0x2c2137],
            Palette::DustByte => [0xf5e9bf, 0xaa644d, 0x788374, 0x372a39],
        };
        unsafe { *PALETTE = quadruple };
    }
}
